ListNode rotateRight(ListNode head, int k) {
    if (head == null) return null;
    ListNode tail = head;
    int len = 1;
    while (tail.next != null) {
        tail = tail.next;
        len++;
    }
    k %= len;
    if (k == 0) return head;
    tail.next = head;
    int steps = len - k;
    ListNode curr = head;
    for (int i = 1; i < steps; i++) curr = curr.next;
    ListNode newHead = curr.next;
    curr.next = null;
    return newHead;
}

